				constraint rhn_snapshotcc_ccid_nn not null
				constraint rhn_snapshotcc_ccid_fk
					references rhnConfigChannel(id),
	created			date default(sysdate)
				constraint rhn_snapshotcc_creat_nn not null,
	modified		date default(sysdate)
				constraint rhn_snapshotcc_mod_nn not null
)
	storage ( freelists 16 )
	initrans 32;
create unique index rhn_snapshotcc_sid_ccid_uq
	on rhnSnapshotConfigChannel( snapshot_id, config_channel_id )
	tablespace [[4m_tbs]]
	storage ( freelists 16 )
	initrans 32;
create index rhn_snpsht_cc_ccid_sid_idx
on rhnSnapshotConfigChannel ( config_channel_id, snapshot_id )
        tablespace [[4m_tbs]]
        storage ( freelists 16 )
        initrans 32;
create or replace trigger
rhn_snapshotcc_mod_trig
before insert or update on rhnSnapshotConfigChannel
for each row
begin
	:new.modified := sysdate;
end;
/
show errors

select '../rhnsat/tables/rhnConfigChannel_triggers.sql' sql_file from dual;
-- SQL relevant contents from file ../rhnsat/tables/rhnConfigChannel_triggers.sql
create or replace trigger
rhn_confchan_mod_trig
before insert or update on rhnConfigChannel
for each row
begin
	:new.modified := sysdate;
end;
/
show errors
create or replace trigger
rhn_confchan_del_trig
before delete on rhnConfigChannel
for each row
declare
	cursor snapshots is
		select	snapshot_id id
		from	rhnSnapshotConfigChannel
		where	config_channel_id = :old.id;
begin
	for snapshot in snapshots loop
		update rhnSnapshot
			set invalid = lookup_snapshot_invalid_reason('cc_removed')
			where id = snapshot.id;
		delete from rhnSnapshotConfigChannel
			where snapshot_id = snapshot.id
				and config_channel_id = :old.id;
	end loop;
end;
/
show errors

